import React, { useState, useEffect } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { FilterMatchMode, FilterOperator } from 'primereact/api';
import axios from "axios";
import data from "./data_table.json"

const DataTableExample = () => {
  const [posts, setPosts] = useState(data);
  // const [globalFilterValue, setGlobalFilterValue] = useState('');
  const [filters, setFilters] = useState({
    "userId":{ value: null, matchMode: FilterMatchMode.START_WITH },
    "id":{ value: null, matchMode: FilterMatchMode.START_WITH },
    "title":{ value: null, matchMode: FilterMatchMode.START_WITH },
    "body":{ value: null, matchMode: FilterMatchMode.START_WITH }
  });
  // const onGlobalFilterChange = (e) => {
  //   const value = e.target.value;
  //   let _filters = { ...filters };
  //   _filters["global"].value = value;

  //   setFilters(_filters);
  //   setGlobalFilterValue(value);
  // }

  // useEffect(() => {
  //   axios
  //     .get("https://jsonplaceholder.typicode.com/posts")
  //     .then((res) => setPosts(res.data));
  // }, []);

  return (
    <div>
      <DataTable
        value={posts}
        responsiveLayout="scroll"
        paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
        dataKey="id"
        paginator
        emptyMessage="No data found."
        className="datatable-responsive"
        currentPageReportTemplate="Showing {first} to {last} of {totalRecords} posts"
        rows={10}
        showGridlines
        filters={filters}
        filterDisplay="row"
        // loading={}
        // globalFilterFields={}
        // header={}
      >
        <Column field="userId" filter filterPlaceholder="Search by IMEI" sortable header="IMEI"></Column>
        <Column field="id" filter filterPlaceholder="Search by Current Firmware Version" sortable header="Current Firmware Version"></Column>
        <Column field="title" filter sortable header="title"></Column>
        <Column field="check" header="Check"></Column>
        <Column field="body" filter sortable header="body"></Column>
      </DataTable>
    </div>
  );
};

export default DataTableExample;
