import React, { useState, useEffect } from 'react'
import axios from 'axios';

const ShowData_total = () => {

    const [api, setApi] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });

    }, []);

    // data_total check
    const data_total = api.length;

    // acc_on check
    const acc_on = api.filter(obj => {
        return obj.acc == 1;
    })
    const count_acc_on = acc_on.length;
    
  return (
    <div>
        <text class="br">Total Data : {data_total}</text>
        <text class="br">ACC ON : {count_acc_on}</text>
    </div>
  )
}

export default ShowData_total
