import React, { useState, useEffect, useRef } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dropdown } from 'primereact/dropdown';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { ConfirmDialog } from 'primereact/confirmdialog'; // tag
import { Toast } from 'primereact/toast';
import { InputTextarea } from 'primereact/inputtextarea';
import axios from "axios";
import './Style.css';

const Data_Table = () => {

    // Api

    // const api = Call_api()

    const [api, setApi] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });
    }, []);

    const [api_limit, setApi_limit] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/limit")
            .then((res) => {
                setApi_limit(res.data)
                // console.log(res.data)
            });
    }, []);

    // setItems 

    const items = api_limit.map(e => {
        if (e.firmware_current === e.firmware_update) {
            return { ...e, status: "available" };
        }
        else {
            return { ...e, status: "unavailable" };
        }
    })

    // acc_on

    const [acc_on, setAcc_on] = useState([]);
    const count_acc_on = acc_on.length;

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/acc/on")
            .then((res) => {
                setAcc_on(res.data)
                // console.log(res.data)
            });
    }, []);

    // acc_off

    const [acc_off, setAcc_off] = useState([]);
    const count_acc_off = acc_off.length;

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/acc/off")
            .then((res) => {
                setAcc_off(res.data)
                // console.log(res.data)
            });
    }, []);

    // status_on

    const [status_on, setStatus_on] = useState([]);
    const count_status_on = status_on.length;

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/status/on")
            .then((res) => {
                setStatus_on(res.data)
                // console.log(res.data)
            });
    }, []);

    // status_off

    const [status_off, setStatus_off] = useState([]);
    const count_status_off = status_off.length;

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/status/off")
            .then((res) => {
                setStatus_off(res.data)
                // console.log(res.data)
            });
    }, []);

    // Multiple Filtered IMEI

    const [mul_filtered_imei, setMul_filtered_imei] = useState('')

    const object = mul_filtered_imei.split("\n")
    const count_object = object.length
    const z = api.filter(e => {
        return object.includes(e.imei)
    })

    const Func_mul_filtered_imei = (e) => {
        setMul_filtered_imei(e.target.value)
    }

    const Confirm_button = () => {
        if (count_object > 1 && count_object <= 200) {
            setApi_limit(z)
            setFilter_found(z)
            toast.current.show({ severity: 'success', summary: 'Filtered Success', detail: 'Al', life: 2000 });
        }
        else {
            toast.current.show({ severity: 'error', summary: 'Filtered Error', detail: 'Sending Update Success', life: 2000 });
        }
    }

    const Reset_button = () => {
        axios
            .get("http://localhost:3300/realtime_db/limit")
            .then((res) => {
                setApi_limit(res.data)
                // console.log(res.data)
            });
    }

    const Clear_button = () => {
        setMul_filtered_imei('')
    }

    // Status

    const [status, setStatus] = useState('');

    const Receive_status = (e) => {
        const input_status = {
            config_status: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc,
            config_firmware_current: filter_firmware_current
        }
        axios
            .post("http://localhost:3300/realtime_db/status", (input_status))
            .then((res) => {
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/status_count", (input_status))
            .then((res) => {
                setFilter_found(res.data)
            });
        setStatus(e.value)
    }

    // Selection

    const [selectedlist, setSelectedlist] = useState([]);

    // Filtered Found

    const [filter_found, setFilter_found] = useState('');

    // Filter Global

    const [filter_global, setFilter_global] = useState('');
    const [list_global, setList_global] = useState([]);

    const Receive_global = (e) => {

        const input_global = {
            config_global: e.target.value
        }
        axios
            .post("http://localhost:3300/realtime_db/global", (input_global))
            .then((res) => {
                setList_global(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/global/count", (input_global))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_global(e.target.value)
    }

    // Filter imei

    const [filter_imei, setFilter_imei] = useState('');
    const [list_imei, setList_imei] = useState([]);

    const Receive_imei = (e) => {

        const input_imei = {
            config_imei: e.target.value,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc,
            config_firmware_current: filter_firmware_current,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/imei", (input_imei))
            .then((res) => {
                setList_imei(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/imei/count", (input_imei))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_imei(e.target.value)
    }

    // Filter firmware_current

    const [filter_firmware_current, setFilter_firmware_current] = useState('');
    const [list_firmware_current, setList_firmware_current] = useState([]);

    const Receive_firmware_current = (e) => {

        const input_firmware_current = {
            config_firmware_current: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/firmware_current", (input_firmware_current))
            .then((res) => {
                setList_firmware_current(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/firmware_current/count", (input_firmware_current))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_firmware_current(e.target.value)
        setConfig(e.value)

    }

    // Filter Mid

    const [filter_mid, setFilter_mid] = useState('');
    const [list_mid, setList_mid] = useState([]);

    const Receive_mid = (e) => {

        const input_mid = {
            config_mid: e.target.value,
            config_imei: filter_imei,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc,
            config_firmware_current: filter_firmware_current,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/mid", (input_mid))
            .then((res) => {
                setList_mid(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/mid/count", (input_mid))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_mid(e.target.value)
    }

    // Filter ccid

    const [filter_ccid, setFilter_ccid] = useState('');
    const [list_ccid, setList_ccid] = useState([]);

    const Receive_ccid = (e) => {

        const input_ccid = {
            config_ccid: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc,
            config_firmware_current: filter_firmware_current,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/ccid", (input_ccid))
            .then((res) => {
                setList_ccid(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/ccid/count", (input_ccid))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_ccid(e.target.value)
    }

    // Filter vehiclemodel

    const [filter_vehiclemodel, setFilter_vehiclemodel] = useState('');
    const [list_vehiclemodel, setList_vehiclemodel] = useState([]);

    const Receive_vehiclemodel = (e) => {

        const input_vehiclemodel = {
            config_vehiclemodel: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_acc: filter_acc,
            config_firmware_current: filter_firmware_current,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/vehiclemodel", (input_vehiclemodel))
            .then((res) => {
                setList_vehiclemodel(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/vehiclemodel/count", (input_vehiclemodel))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_vehiclemodel(e.target.value)
    }

    // Filter acc

    const [filter_acc, setFilter_acc] = useState('');
    const [list_acc, setList_acc] = useState([]);

    const Receive_acc = (e) => {

        const input_acc = {
            config_acc: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_firmware_current: filter_firmware_current,
            config_status: status
        }
        axios
            .post("http://localhost:3300/realtime_db/acc", (input_acc))
            .then((res) => {
                setList_acc(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/acc/count", (input_acc))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_acc(e.target.value)
        setSl_acc(e.value)
    }

    // Function Check_filter_found

    const Func_count_filter_found = () => {
        if (filter_imei == '' && filter_mid == '' && filter_firmware_current == '' && filter_ccid == '' && filter_vehiclemodel == '' && filter_acc == '' && filter_global == '' && status == '' && mul_filtered_imei == '') {
            return '-'
        }
        else {
            return filter_found.length;
        }
    }
    const count_filter_found = Func_count_filter_found()

    // console.log(filter_imei)
    // console.log(filter_found)
    // console.log(count_filter_found)

    const count_data_total = api.length

    // Render filter

    const render_filter_global = () => {
        return (
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText value={filter_global} onChange={Receive_global} placeholder="Search By Global"></InputText>
            </span>
        )
    }

    const global_text = render_filter_global()

    const render_filter_imei = () => {
        return (
            <InputText value={filter_imei} onChange={Receive_imei} placeholder="Search By IMEI"></InputText>
        )
    }

    const render_filter_mid = () => {
        return (
            <InputText value={filter_mid} onChange={Receive_mid} placeholder="Search By MID"></InputText>
        )
    }

    const render_filter_ccid = () => {
        return (
            <InputText value={filter_ccid} onChange={Receive_ccid} placeholder="Search By CCID"></InputText>
        )
    }

    const render_filter_vehiclemodel = () => {
        return (
            <InputText value={filter_vehiclemodel} onChange={Receive_vehiclemodel} placeholder="Search By Vehiclemodel"></InputText>
        )
    }

    const render_filter_acc = () => {
        return (
            <Dropdown value={sl_acc} options={Acc_SelectItems} onChange={Receive_acc} placeholder="Select ACC"></Dropdown>
        )
    }

    const render_filter_firmware_current = () => {
        return (
            <Dropdown value={config} options={use_list_cfv} onChange={Receive_firmware_current} placeholder="Select Version"></Dropdown>
        )
    }

    const render_config = () => {
        return (
            <Dropdown value={version} options={use_list_cfv} onChange={(e) => setVersion(e.value)} placeholder="Select Version" />
        )
    }

    const statuses = [
        'available', 'unavailable'
    ];

    const status_option = [
        { label: 'available', value: 'available' },
        { label: 'unavailable', value: 'unavailable' }
    ];

    const statusBody = (rowData) => {
        return <span className={`customer-badge status-${rowData.status}`}>{rowData.status}</span>;
    }

    const cfv_filter_Body = (rowData) => {
        return <span>{rowData.firmware_current}</span>;
    }

    const statusItem = (option) => {
        return <span className={`customer-badge status-${option}`}>{option}</span>;
    }

    const render_status = () => {
        return <Dropdown value={status} options={status_option} onChange={Receive_status} placeholder="Select a Status" />;
        // return <Dropdown value={status} options={status_option} onChange={Receive_status} itemTemplate={statusItem} placeholder="Select a Status"/>;
    }

    // Firmware Selection

    const [version, setVersion] = useState(null);
    const [config, setConfig] = useState(null);
    const [sl_acc, setSl_acc] = useState(null);

    const Acc_SelectItems = [
        { label: '0', value: '0' },
        { label: '1', value: '1' }
    ];

    const [list_cfv, setList_cfv] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/cfv")
            .then((res) => {
                setList_cfv(res.data)
                // console.log(res.data)
            });
    }, []);

    const use_list_cfv = list_cfv.map(e => {
        return { label: e.firmware_current, value: e.firmware_current }
    })

    const input_select = (x) => {
        return x = version
    }

    // Clear Filter

    const clearFilter = () => {
        initFilters();
        axios
            .get("http://localhost:3300/realtime_db/limit")
            .then((res) => {
                setApi_limit(res.data)
                // console.log(res.data)
            });
    }

    const initFilters = () => {
        setFilter_global('');
        setFilter_imei('');
        setFilter_mid('');
        setFilter_ccid('');
        setFilter_vehiclemodel('');
        setFilter_firmware_current('');
        setFilter_acc('');
        setStatus('')
        setSl_acc(null);
        setConfig(null);
        setVersion(null);
        setSelectedlist([]);
    }

    const renderClear = () => {
        return (
            <div>
                <Button type="button" icon="pi pi-filter-slash" label="Clear" className="p-button-outlined" onClick={clearFilter} />
            </div>
        )
    }

    const clear_filter = renderClear()

    // Toast

    const toast = useRef(null);

    const showError = () => {
        toast.current.show({ severity: 'error', summary: 'Update Error', detail: 'Please select data and config', life: 2000 });
    }

    // Sending
    const count_selectedlist = selectedlist.length

    const dataUpdate = {
        id: selectedlist,
        version: version
    }
    const Sending = () => {
        if (count_selectedlist <= 100) {
            axios
                .put("http://localhost:3300/realtime_db/update", (dataUpdate))
            toast.current.show({ severity: 'success', summary: 'Update Success', detail: 'Sending Update Success', life: 2000 });
        }
        else {
            toast.current.show({ severity: 'error', summary: 'Update Error', detail: 'Plase select data less than or equal to 100', life: 2000 });
        }
    }

    // Button Update All

    const Button_Update = () => {

        const [visible, setVisible] = useState();
        const [reject, setReject] = useState();

        if (version != null && count_selectedlist != 0) {
            return (
                <>
                    <Toast ref={toast} />
                    <ConfirmDialog visible={visible} onHide={() => setVisible(false)} message="Are you sure you want to proceed?"
                        header="Confirmation" icon="pi pi-exclamation-triangle" accept={Sending} reject={reject} />
                    <Button label="Update All" onClick={() => setVisible(true)} className="p-button p-component p-button-outlined"></Button>
                </>
            )
        }
        else {
            return (
                <>
                    <Toast ref={toast} />
                    <Button label="Update All" onClick={showError} className="p-button p-component p-button-outlined"></Button>
                </>
            );
        }

    }

    const btu = Button_Update();

    // renderHeader

    const renderHeader = () => {
        return (
            <div class="row dbal">
                <div class="col-6">
                    <div className="flex">
                        {global_text}
                        <div class="bl">
                            {clear_filter}
                        </div>
                    </div>
                </div>
                <div class="col-6">
                    <div className="flex justify-content-end">
                        {btu}
                    </div>
                </div>
            </div>
        )
    }
    const header = renderHeader();

    // Test 

    const render_mul = () => {
        if (count_object == 0) {
            return (
                <div className="flex">
                    <text class="dt">filtered count : 0 / 200 </text>
                </div>
            )
        }
        else if (count_object <= 200) {
            return (
                <div className="flex">
                    <text class="dt">filtered count : {count_object - 1} / 200 </text>
                </div>
            )
        }
        else {
            return (
                <div className="flex">
                    <text class="red dt">filtered count : {count_object - 1} / 200 </text>
                </div>
            )
        }
    }

    const mul = render_mul();

    return (
        <div class="datatable-filter-real">
            <div class="row">
                <div class="col-2">
                    <text class="dt"><b>MULTIPLE FILTERED IMEI</b></text>
                    <div className="flex">
                        <InputTextarea rows={3} cols={32} value={mul_filtered_imei} onChange={Func_mul_filtered_imei}></InputTextarea>
                    </div>
                    {mul}
                    <Button className="p-button-info" label="Filtered" onClick={Confirm_button}></Button>
                    <Button className="bleft p-button-danger" label="Clear" onClick={Clear_button}></Button>
                    <Button className="rbleft p-button-warning" label="Reset" onClick={Reset_button}></Button>
                </div>
                <div class="col-2">
                    <div className="top card bottom btu col">
                        <text class="dt"><b>TABLE VIEW</b> : 1000</text>
                        <text class="dt"><b>FILTERED FOUND</b> : {count_filter_found}</text>
                        <text class="dt"><b>DATA TOTAL</b> : {count_data_total}</text>
                    </div>
                </div>
                <div class="col-2">
                    <div className="top card bottom btu col">
                        <text class="dt"><b>ACC CHECK</b></text>
                        <text class="dt"><b>ACC ON</b> : {count_acc_on}</text>
                        <text class="dt"><b>ACC OFF</b> : {count_acc_off}</text>
                    </div>
                </div>
                <div class="col-2">
                    <div className="top card bottom btu col">
                        <text class="dt"><b>STATUS CHECK</b></text>
                        <text class="dt"><b>STATUS ON</b> : {count_status_on}</text>
                        <text class="dt"><b>STATUS OFF</b> : {count_status_off}</text>
                    </div>
                </div>
                <div class="col-4">
                    <div class="d-grid d-md-flex justify-content-md-end">
                        <a href="http://localhost:3000/" >
                            <Button label="Refresh" className="p-button-success"></Button>
                        </a>
                    </div>
                </div>
            </div>

            <DataTable
                value={items}
                responsiveLayout="scroll"
                paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                rowsPerPageOptions={[10, 50, 100, 200, 500, 1000]}
                dataKey="id"
                paginator
                emptyMessage="No data found."
                className="datatable-responsive"
                currentPageReportTemplate="Showing {first} to {last} of Filter Found : {totalRecords} data."
                rows={10}
                showGridlines
                // filters={filters}
                filterDisplay="row"
                header={header}
                resizableColumns
                columnResizeMode="expand"
                selectionMode="checkbox" selection={selectedlist} onSelectionChange={e => setSelectedlist(e.value)}
                paginatorPosition="bottom"
                alwaysShowPaginator
            // size="small"
            // globalFilterFields={['IMEI', 'CFV.IMEI', 'UFV.IMEI', 'status']}
            // loading={}
            >
                <Column field="imei" filter filterElement={render_filter_imei} filterPlaceholder="Search by IMEI" sortable header="IMEI"></Column>
                <Column field="mid" filter filterElement={render_filter_mid} filterPlaceholder="Search by MID" sortable header="MID"></Column>
                <Column field="ccid" filter filterElement={render_filter_ccid} filterPlaceholder="Search by CCID" sortable header="CCID"></Column>
                <Column field="vehiclemodel" filter filterElement={render_filter_vehiclemodel} filterPlaceholder="Search by Vehiclemodel" sortable header="Vehiclemodel"></Column>
                <Column field="acc" filter filterElement={render_filter_acc} filterPlaceholder="Search by ACC" sortable header="ACC"></Column>
                <Column field="status" header="Status Check" body={statusBody} filter filterElement={render_status} />
                <Column field="firmware_current" header="Current Firmware Version" showFilterMenu={false} filterMenuStyle={{ width: '14rem' }} style={{ minWidth: '12rem' }} body={cfv_filter_Body} filter filterElement={render_filter_firmware_current} sortable />
                <Column body={input_select} header="Configure" filter filterElement={render_config}></Column>
                <Column header="Selection Data" selectionMode="multiple"></Column>
            </DataTable>
        </div>
    )
};


export default Data_Table;
