import React, { useState, useEffect } from 'react'
import axios from 'axios';

const showData_total = () => {

   const [api, setApi] = useState([]);
    const data_total = api.length;

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });

    }, []);

    console.log(api)

    return (
        <div><text class="br">Total Data : {data_total}</text></div>
    )
}

export default showData_total
