import React, { useState, useEffect } from 'react';
import { Chart } from 'primereact/chart';
import axios from 'axios';

const DoughnutChartDemo = () => {

    // api database

    const [api, setApi] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });

    }, []);
    
    

    const acc_on = api.filter(obj => {
        return obj.acc == 1;
    })
    const Convert = () => {
        const [x, setX] = useState("");
        const y = acc_on.length;
        setX(y);
    }
    

    const [chartData] = useState({
        labels: ['A', 'B', 'C'],
        datasets: [
            {
                data: [12,5],
                backgroundColor: [
                    "#FF6384",
                    "#36A2EB",
                    "#FFCE56"
                ],
                hoverBackgroundColor: [
                    "#FF6384",
                    "#36A2EB",
                    "#FFCE56"
                ]
            }]
    });

    const [lightOptions] = useState({
        plugins: {
            legend: {
                labels: {
                    color: '#495057'
                }
            }
        }
    });
    console.log()

    return (
        <div className="flex justify-content-center">
            <Chart type="doughnut" data={chartData} options={lightOptions} style={{ position: 'relative', width: '10%' }} />
        </div>
    )
}

export default DoughnutChartDemo;
