import React from 'react'
import { Button } from 'primereact/button';
import { ConfirmDialog } from 'primereact/confirmdialog'; // tag
import { confirmDialog } from 'primereact/confirmdialog'; // method
import './Styles.css';

function Button_UpdateAll() {

    const confirm = () => {
        confirmDialog({
            message: 'Are you sure you want to proceed?',
            header: 'Confirmation',
            icon: 'pi pi-exclamation-triangle',
        });
    }

    return (
        <>
            <div class="button-update-all d-grid gap-2 d-md-flex justify-content-md-end">
                <a class="rf p-button p-component p-button-lg p-button-danger" href="http://localhost:3000/">Refresh</a>
                {/* <Button label="Update All" className="p-button p-component p-button-lg p-button-success"></Button> */}
                <Button onClick={confirm} icon="pi pi-check" label="Confirm"></Button>
            </div>
        </>
    )
}

export default Button_UpdateAll;
