import React, { useState } from 'react'
import { Button, Table } from 'reactstrap';
import '../App.css';
import data from "./data.json"



function create_circle(x) {
    if (x == 1) {
        return (<center><div class="circle1"></div></center>)
    }
    else {
        return (<center><div class="circle2"></div></center>)
    }
}

function create_input(value) {
    return (<input type={'text'} name='EMEI' defaultValue={value}></input>)
}

function Data_table() {

    const [datatable, setDatatable] = useState(data);
    const [order, setOrder] = useState("ASC");
    const sorting =(col) => {
        if (order === "ASC"){
            const sorted = [...data].sort((a,b) =>
                a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
            );
            setDatatable(sorted);
            setOrder("DSC");
        }
        if (order === "DSC"){
            const sorted = [...data].sort((a,b) =>
                a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
            );
            setDatatable(sorted);
            setOrder("ASC");
        }
    }
    

    return (
        <Table responsive>
            <thead>
                <tr>
                    <th onClick={() => sorting("imei")}>
                        IMEI
                        
                    </th>
                    <th>CHECK</th>
                    <th onClick={() => sorting("fvc")}>FVC</th>
                    <th>FVU</th>
                    <th>CONFIG</th>
                </tr>
            </thead>
            <tbody bgcolor="#FFFFFF">
                <tr>
                    <td>
                        <input type={'text'} name='EMEI'></input>
                    </td>
                    <td></td>
                    <td>
                        <input type={'text'} name='EMEI'></input>
                    </td>
                    <td></td>
                    <td></td>
                </tr>
                {datatable.map((x) => (
                    <tr>
                        <td>{x.imei}</td>
                        <td>{create_circle(x.check)}</td>
                        <td>{x.fvc}</td>
                        <td>{create_input(x.fvu)}</td>
                        <td><Button class="btn btn-primary btn-sm" color="primary">UPDATE</Button></td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
}
export default Data_table;

