import React, { useState } from 'react'
import { Button, UncontrolledPopover, PopoverHeader, PopoverBody, Table } from 'reactstrap';
import '../App.css';
import data from "./data.json"




function Tabler() {

    const [datat, setDatat] = useState(data);

    return (
            <Table responsive>
                <thead bgcolor="#212529">
                    <tr>
                        <th>IMEI</th>
                        <th>CHECK</th>
                        <th>FVC</th>
                        <th>FVU</th>
                        <th>CONFIG</th>
                    </tr>
                </thead>
                <tbody bgcolor="#FFFFFF">
                    <tr>
                        <td>
                            <input type={'text'} name='EMEI'></input>
                        </td>
                        <td></td>
                        <td>
                            <input type={'text'} name='EMEI'></input>
                        </td>
                        <td></td>
                        <td></td>
                    </tr>
                    <tr>
                        {datat.map((datat))}
                    </tr>
                </tbody>
            </Table>
    );
}

