import React from 'react'
import { Button } from 'primereact/button';
import { ConfirmDialog } from 'primereact/confirmdialog'; // tag
import { confirmDialog } from 'primereact/confirmdialog'; // method
import './Styles.css';

function Button_UpdateAll() {

    const Refresh_Page = () => {
        return (
            <a href='http://localhost:3000/'></a>
        )
    }
    return (
        <>
            <div class="button-update-all d-grid gap-2 d-md-flex justify-content-md-end">
                <a class="rf p-button p-component p-button-lg p-button-danger" href="http://localhost:3000/">Refresh</a>
                <ConfirmDialog visible={visible} onHide={() => setVisible(false)} message="Are you sure you want to proceed?"
                    header="Confirmation" icon="pi pi-exclamation-triangle" accept={accept} reject={reject} />
                <Button label="Update All" onClick={() => setVisible(true)} className="p-button p-component p-button-lg p-button-success" data-bs-toggle="modal" data-bs-target="#updateAll"></Button>
            </div>
        </>
    )
}

export default Button_UpdateAll;
