import React, { useState, useEffect } from 'react';
import { Chart } from 'primereact/chart';
import axios from 'axios';
import data from './data_table.json'

const DoughnutChartDemo = () => {

    // api database

    const [api, setApi] = useState(data);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });

    }, []);
    

    
    const acc_on = api.filter(obj => {
        return obj.acc == 1;
    })
    // const x = acc_on.length;
    // const [count ,setCount] = useState(x);
    const y = data.length
    const x = acc_on.length;
    console.log(x,y)




    const [chartData] = useState({
        labels: ['A', 'B', 'C'],
        datasets: [
            {
                data: [x,2,8],
                backgroundColor: [
                    "#FF6384",
                    "#36A2EB",
                    "#FFCE56"
                ],
                hoverBackgroundColor: [
                    "#FF6384",
                    "#36A2EB",
                    "#FFCE56"
                ]
            }]
    });

    const [lightOptions] = useState({
        plugins: {
            legend: {
                labels: {
                    color: '#495057'
                }
            }
        }
    });

    return (
        <div className="flex justify-content-center">
            <Chart type="pie" data={chartData} options={lightOptions} style={{ position: 'relative', width: '20%' }} />
        </div>
    )
}

export default DoughnutChartDemo;
