import React from 'react'
import { Button } from 'primereact/button';
import './Styles.css';

function Button_UpdateAll() {

    const Refresh_Page = () => {
        return (
            <a href='http://localhost:3000/'></a>
        )
    }
    return (
        <div>
            <div class="button-update-all d-grid gap-2 d-md-flex justify-content-md-end">
                <a class="rf p-button p-component p-button-lg p-button-danger" href="http://localhost:3000/">Refresh</a>
                <Button label="Update All" className="p-button p-component p-button-lg p-button-success" data-bs-toggle="modal" data-bs-target="#authbutton1"></Button>
                <div class="modal fade" id="authbutton1" tabindex="-1" aria-labelledby="authbutton1Label" aria-hidden="true">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header bg-dark text-white">
                                <h5 class="modal-title ms-auto" id="authbutton1Label">Update All Confirm?</h5>
                                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div class="d-grid gap-3 d-md-flex justify-content-md-center modal-body">
                                <button type="button" class="btn btn-primary" data-bs-dismiss="modal">Confirm</button>
                                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Button_UpdateAll;
