import React, { useState, useEffect, useRef } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dropdown } from 'primereact/dropdown';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { MultiSelect } from 'primereact/multiselect';
import { ConfirmDialog } from 'primereact/confirmdialog'; // tag
import { FilterMatchMode } from 'primereact/api';
import { Toast } from 'primereact/toast';
import axios from "axios";
import './Style.css';

// const Call_api = () => {

//     const [api, setApi] = useState([]);

//     useEffect(() => {
//         axios
//             .get("http://localhost:3300/realtime_db")
//             .then((res) => {
//                 setApi(res.data)
//                 // console.log(res.data)
//             });
//     }, []);
//     // console.log(api)
//     return api
// }

const Data_Table = () => {

    // Api

    // const api = Call_api()

    const [api, setApi] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });
    }, []);

    const [api_limit, setApi_limit] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db/limit")
            .then((res) => {
                setApi_limit(res.data)
                // console.log(res.data)
            });
    }, []);

    // setItems 

    const items = api_limit.map(e => {
        if (e.firmware_current === e.firmware_update) {
            return { ...e, status: "available" };
        }
        else {
            return { ...e, status: "unavailable" };
        }
    })

    // Selection

    const [selectedlist, setSelectedlist] = useState(null);
    // console.log(selectedlist)

    // Filtered Found

    const [filter_found, setFilter_found] = useState('');

    // Filter imei

    const [filter_imei, setFilter_imei] = useState('');
    const [list_imei, setList_imei] = useState([]);

    const Receive_imei = (e) => {

        const input_imei = {
            config_imei: e.target.value,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_firmware_current: filter_firmware_current,
            config_acc: filter_acc
        }
        axios
            .post("http://localhost:3300/realtime_db/imei", (input_imei))
            .then((res) => {
                setList_imei(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/imei/count", (input_imei))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_imei(e.target.value)
    }

    // console.log(list_imei)
    // console.log(filter_imei)

    // Filter firmware_current

    const [filter_firmware_current, setFilter_firmware_current] = useState('');
    const [list_firmware_current, setList_firmware_current] = useState([]);

    const Receive_firmware_current = (e) => {

        const input_firmware_current = {
            config_firmware_current: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_acc: filter_acc
        }
        axios
            .post("http://localhost:3300/realtime_db/firmware_current", (input_firmware_current))
            .then((res) => {
                setList_firmware_current(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/firmware_current/count", (input_firmware_current))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_firmware_current(e.target.value)
        setConfig(e.value)

    }
    // console.log(list_firmware_current)
    // console.log(filter_firmware_current)

    // Filter Mid

    const [filter_mid, setFilter_mid] = useState('');
    const [list_mid, setList_mid] = useState([]);

    const Receive_mid = (e) => {

        const input_mid = {
            config_mid: e.target.value,
            config_imei: filter_imei,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_firmware_current: filter_firmware_current,
            config_acc: filter_acc
        }
        axios
            .post("http://localhost:3300/realtime_db/mid", (input_mid))
            .then((res) => {
                setList_mid(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/mid/count", (input_mid))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_mid(e.target.value)
    }

    // Filter ccid

    const [filter_ccid, setFilter_ccid] = useState('');
    const [list_ccid, setList_ccid] = useState([]);

    const Receive_ccid = (e) => {

        const input_ccid = {
            config_ccid: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_vehiclemodel: filter_vehiclemodel,
            config_firmware_current: filter_firmware_current,
            config_acc: filter_acc
        }
        axios
            .post("http://localhost:3300/realtime_db/ccid", (input_ccid))
            .then((res) => {
                setList_ccid(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/ccid/count", (input_ccid))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_ccid(e.target.value)
    }

    // Filter vehiclemodel

    const [filter_vehiclemodel, setFilter_vehiclemodel] = useState('');
    const [list_vehiclemodel, setList_vehiclemodel] = useState([]);

    const Receive_vehiclemodel = (e) => {

        const input_vehiclemodel = {
            config_vehiclemodel: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_firmware_current: filter_firmware_current,
            config_acc: filter_acc
        }
        axios
            .post("http://localhost:3300/realtime_db/vehiclemodel", (input_vehiclemodel))
            .then((res) => {
                setList_vehiclemodel(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/vehiclemodel/count", (input_vehiclemodel))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_vehiclemodel(e.target.value)
    }

    // Filter acc

    const [filter_acc, setFilter_acc] = useState('');
    const [list_acc, setList_acc] = useState([]);

    const Receive_acc = (e) => {

        const input_acc = {
            config_acc: e.target.value,
            config_imei: filter_imei,
            config_mid: filter_mid,
            config_ccid: filter_ccid,
            config_vehiclemodel: filter_vehiclemodel,
            config_firmware_current: filter_firmware_current
        }
        axios
            .post("http://localhost:3300/realtime_db/acc", (input_acc))
            .then((res) => {
                setList_acc(res.data)
                setApi_limit(res.data)
            });

        axios
            .post("http://localhost:3300/realtime_db/acc/count", (input_acc))
            .then((res) => {
                setFilter_found(res.data)
            });

        setFilter_acc(e.target.value)
    }

    // Function Check_filter_found

    const Func_count_filter_found = () => {
        if (filter_imei == '' && filter_mid == '' && filter_firmware_current == '') {
            return '-'
        }
        else {
            return filter_found.length;
        }
    }
    const count_filter_found = Func_count_filter_found()

    console.log(filter_imei)
    console.log(filter_found)
    console.log(count_filter_found)

    const count_data_total = api.length

    // Render filter

    const render_filter_imei = () => {
        return (
            <InputText value={filter_imei} onChange={Receive_imei} placeholder="Search By IMEI"></InputText>
        )
    }

    const render_filter_mid = () => {
        return (
            <InputText value={filter_mid} onChange={Receive_mid} placeholder="Search By MID"></InputText>
        )
    }

    const render_filter_firmware_current = () => {
        return (
            <Dropdown value={config} options={versionSelectItems} onChange={Receive_firmware_current} placeholder="Select Version"></Dropdown>
        )
    }

    const render_filter_ccid = () => {
        return (
            <InputText value={filter_ccid} onChange={Receive_ccid} placeholder="Search By CCID"></InputText>
        )
    }

    const render_filter_vehiclemodel = () => {
        return (
            <InputText value={filter_vehiclemodel} onChange={Receive_vehiclemodel} placeholder="Search By Vehiclemodel"></InputText>
        )
    }

    const render_filter_ = () => {
        return (
            <InputText value={filter_ccid} onChange={Receive_ccid} placeholder="Search By CCID"></InputText>
        )
    }

    // Filter

    const [filters, setFilters] = useState({
        "imei": { value: null, matchMode: FilterMatchMode.START_WITH },
        "mid": { value: null, matchMode: FilterMatchMode.START_WITH },
        "ccid": { value: null, matchMode: FilterMatchMode.START_WITH },
        "firmware_current": { value: null, matchMode: FilterMatchMode.EQUALS },
        "firmware_update": { value: null, matchMode: FilterMatchMode.EQUALS },
        "vehiclemodel": { value: null, matchMode: FilterMatchMode.START_WITH },
        "acc": { value: null, matchMode: FilterMatchMode.START_WITH },
        "status": { value: null, matchMode: FilterMatchMode.EQUALS },
        "global": { value: null, matchMode: FilterMatchMode.CONTAINS }
    });

    const cfv_filter = [
        'HLT500_V306701', 'HLT500_V306702', 'HLT500_V306703', 'HLT500_V306704', 'HLT500_V306705', 'HLT500_V306706', 'HLT500_V306707',
        'HLT500_V306708', 'HLT500_V306709'
    ]

    const ufv_filter = [
        'HLT500_V306701', 'HLT500_V306702', 'HLT500_V306703', 'HLT500_V306704', 'HLT500_V306705', 'HLT500_V306706', 'HLT500_V306707',
        'HLT500_V306708', 'HLT500_V306709'
    ]

    const statuses = [
        'available', 'unavailable'
    ];

    const statusBody = (rowData) => {
        return <span className={`customer-badge status-${rowData.status}`}>{rowData.status}</span>;
    }

    const cfv_filter_Body = (rowData) => {
        return <span>{rowData.firmware_current}</span>;
    }

    const ufv_filter_Body = (rowData) => {
        return <span>{rowData.firmware_update}</span>;
    }

    const statusItem = (option) => {
        return <span className={`customer-badge status-${option}`}>{option}</span>;
    }

    const cfv_filter_Item = (option) => {
        return <span>{option}</span>;
    }

    const ufv_filter_Item = (option) => {
        return <span>{option}</span>;
    }
    const statusRowFilter = (options) => {
        return <Dropdown value={options.value} options={statuses} onChange={(e) => options.filterApplyCallback(e.value)} itemTemplate={statusItem} placeholder="Select a Status" className="p-column-filter" showClear />;
    }

    const cfv_filter_RowFilter = (options) => {
        return <Dropdown value={options.value} options={cfv_filter} onChange={(e) => options.filterApplyCallback(e.value)} itemTemplate={cfv_filter_Item} placeholder="Select a Status" className="p-column-filter" showClear />;
    }

    const ufv_filter_RowFilter = (options) => {
        return <Dropdown value={options.value} options={ufv_filter} onChange={(e) => options.filterApplyCallback(e.value)} itemTemplate={ufv_filter_Item} placeholder="Select a Status" className="p-column-filter" showClear />;
    }

    const dropdownUpdate = () => {
        return <Dropdown value={version} options={versionSelectItems} onChange={(e) => setVersion(e.value)} placeholder="Select Version" />
    }

    // Firmware Selection

    const [version, setVersion] = useState(null);
    const [config, setConfig] = useState(null);
    const versionSelectItems = [
        { label: 'HLT500_V306709', value: 'HLT500_V306709' },
        { label: 'HLT500_V306708', value: 'HLT500_V306708' },
        { label: 'HLT500_V306707', value: 'HLT500_V306707' },
        { label: 'HLT500_V306706', value: 'HLT500_V306706' },
        { label: 'HLT500_V306705', value: 'HLT500_V306705' },
        { label: 'HLT500_V306704', value: 'HLT500_V306704' },
        { label: 'HLT500_V306703', value: 'HLT500_V306703' },
        { label: 'HLT500_V306702', value: 'HLT500_V306702' },
        { label: 'HLT500_V306701', value: 'HLT500_V306701' },
    ];
    const input_select = (x) => {
        return x = version
    }

    // Global Filter

    const [globalFilterValue, setGlobalFilterValue] = useState(null);

    const onGlobalFilterChange = (e) => {
        const value = e.target.value;
        let _filters = { ...filters };
        _filters['global'].value = value;

        setFilters(_filters);
        setGlobalFilterValue(value);
    }

    // Clear Filter

    const clearFilter = () => {
        initFilters();
    }

    const initFilters = () => {
        setFilters({
            "imei": { value: null, matchMode: FilterMatchMode.START_WITH },
            "mid": { value: null, matchMode: FilterMatchMode.START_WITH },
            "ccid": { value: null, matchMode: FilterMatchMode.START_WITH },
            "firmware_current": { value: null, matchMode: FilterMatchMode.EQUALS },
            "firmware_update": { value: null, matchMode: FilterMatchMode.EQUALS },
            "vehiclemodel": { value: null, matchMode: FilterMatchMode.START_WITH },
            "acc": { value: null, matchMode: FilterMatchMode.START_WITH },
            "status": { value: null, matchMode: FilterMatchMode.EQUALS },
            "global": { value: null, matchMode: FilterMatchMode.CONTAINS }
        });
        setGlobalFilterValue('');
        setVersion(null);
    }

    // Advance Filter

    const [selectedColumns, setSelectedColumns] = useState([]);

    const columns = [
        { field: 'mid', header: 'MID' },
        { field: 'ccid', header: 'CCID' },
        { field: 'vehiclemodel', header: 'Vehiclemodel' },
        { field: 'acc', header: 'ACC' }
    ];

    const onColumnToggle = (event) => {
        let selectedColumns = event.value;
        let orderedSelectedColumns = columns.filter(col => selectedColumns.some(sCol => sCol.field === col.field));
        setSelectedColumns(orderedSelectedColumns);
    }

    const Advance_filter = () => {
        return (
            <div style={{ textAlign: 'left' }}>
                <MultiSelect value={selectedColumns} placeholder="Advance Filter" options={columns} optionLabel="header" onChange={onColumnToggle} style={{ width: '20em' }} filter />
            </div>
        );
    }

    const adf = Advance_filter();

    const columnComponents = selectedColumns.map(col => {
        // <Column field="imei" filter filterElement={render_filter_imei} filterPlaceholder="Search by IMEI" sortable header="IMEI"></Column>
        return (
            <Column field={col.field} header={col.header} filter filterElement={render_filter_mid} sortable />
        )
    });

    // Radio Button Table View

    // const [amount, setAmount] = useState();
    // // console.log(amount)
    // const RadioButtonDemo = () => {
    //     return (
    //         <div className="field-radiobutton">
    //             <text class="br">Table View : </text>
    //             <RadioButton inputId="amount1" name="amount" value="5" onChange={(e) => setAmount(e.value)} checked={amount === '5'} />
    //             <label class="br" htmlFor="amount1">5</label>
    //             <RadioButton inputId="amount2" name="amount" value="10" onChange={(e) => setAmount(e.value)} checked={amount === '10'} />
    //             <label class="br" htmlFor="amount2">10</label>
    //             <RadioButton inputId="amount3" name="amount" value="20" onChange={(e) => setAmount(e.value)} checked={amount === '20'} />
    //             <label class="br" htmlFor="amount3">20</label>
    //         </div>
    //     )
    // }
    // const rdbt = RadioButtonDemo();

    // Toast

    const toast = useRef(null);

    const showSuccess = () => {
        toast.current.show({ severity: 'success', summary: 'Success Message', detail: 'Message Content', life: 3000 });
    }

    const showError = () => {
        toast.current.show({ severity: 'error', summary: 'Error Message', detail: 'Please Select Configure and Data', life: 2000 });
    }

    // Sending

    const dataUpdate = {
        id: selectedlist,
        version: version
    }
    const Sending = () => {
        axios
            .put("http://localhost:3300/realtime_db/update", (dataUpdate))
    }
    // console.log(version)

    // Button Update All

    const Button_Update = () => {

        const [visible, setVisible] = useState();
        const [reject, setReject] = useState();

        if (selectedlist == null || selectedlist == [] && version == null) {
            return (
                <>
                    <Toast ref={toast} />
                    <Button label="Update All" onClick={showError} className="p-button p-component p-button-outlined"></Button>
                </>
            )
        }
        else {
            return (
                <>
                    <Toast ref={toast} />
                    <ConfirmDialog visible={visible} onHide={() => setVisible(false)} message="Are you sure you want to proceed?"
                        header="Confirmation" icon="pi pi-exclamation-triangle" accept={Sending} reject={reject} />
                    <Button label="Update All" onClick={() => setVisible(true)} className="p-button p-component p-button-outlined"></Button>
                </>
            );
        }

    }

    const btu = Button_Update();

    // renderHeader

    const renderHeader = () => {
        return (
            <div className="flex justify-content-between">{adf}{btu}</div>
        )
    }
    const header = renderHeader();

    return (
        <div class="datatable-filter-real">
            <div className="btu flex justify-content-between">
                <div className="card bottom col-2 btu flex justify-content-between">
                    <text class="dt"><b>Table View</b> : 1000</text>
                    <text class="dt"><b>Filtered Found</b> : {count_filter_found}</text>
                    <text class="dt"><b>Data Total</b> : {count_data_total}</text>
                </div>
                <div class="d-grid d-md-flex justify-content-md-end">
                    <a href="http://localhost:3000/" >
                        <Button label="Refresh" className="p-button-danger"></Button>
                    </a>
                </div>
            </div>
            {/* <Button label="Send" onClick={Receive}></Button> */}
            {/* <text class="dt top">Filter Found : {count_filter}</text> */}
            {/* <InputText value={filter_firmware_current} onChange={Receive_firmware_current} placeholder="Search Filter firmware_update"></InputText> */}
            {/* <Dropdown value={version} options={versionSelectItems} onChange={Receive_firmware_current} placeholder="Select Version"></Dropdown> */}

            <DataTable
                value={items}
                responsiveLayout="scroll"
                paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
                dataKey="id"
                paginator
                emptyMessage="No data found."
                className="datatable-responsive"
                currentPageReportTemplate="Showing {first} to {last} of Filter Found : {totalRecords} data."
                rows={10}
                showGridlines
                // filters={filters}
                filterDisplay="row"
                header={header}
                resizableColumns
                columnResizeMode="expand"
                selectionMode="checkbox" selection={selectedlist} onSelectionChange={e => setSelectedlist(e.value)}
                paginatorPosition="bottom"
                alwaysShowPaginator
            // size="small"
            // globalFilterFields={['IMEI', 'CFV.IMEI', 'UFV.IMEI', 'status']}
            // loading={}
            >
                <Column field="imei" filter filterElement={render_filter_imei} filterPlaceholder="Search by IMEI" sortable header="IMEI"></Column>
                {columnComponents}
                {/* <Column field="status" header="Status Check" showFilterMenu={false} filterMenuStyle={{ width: '14rem' }} style={{ minWidth: '12rem' }} body={statusBody} filter filterElement={statusRowFilter} /> */}
                <Column field="status" header="Status Check" showFilterMenu={false} filterMenuStyle={{ width: '14rem' }} style={{ minWidth: '12rem' }} body={statusBody} />
