import React, { useState, useEffect, useRef, version } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dropdown } from 'primereact/dropdown';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { MultiSelect } from 'primereact/multiselect';
import { ConfirmDialog } from 'primereact/confirmdialog'; // tag
import { RadioButton } from 'primereact/radiobutton';
// import { MultiSelect } from 'primereact/multiselect';
import { FilterMatchMode, FilterOperator } from 'primereact/api';
import axios from "axios";
import data from "./data_table.json"
import "./data_table.css";
import "./Styles.css"


const DataTableExample = () => {
  const [products, setProducts] = useState(data);
  // console.log(products)

  // useEffect(() => {
  //   axios
  //     .get("https://jsonplaceholder.typicode.com/posts")
  //     .then((res) => {
  //       console.log(res.data)
  //       setProducts(data)
  //     }
  //       );
  // }, []);

  const [filters, setFilters] = useState({
    "IMEI": { value: null, matchMode: FilterMatchMode.START_WITH },
    "CFV": { value: null, matchMode: FilterMatchMode.START_WITH },
    "Status": { value: null, matchMode: FilterMatchMode.EQUALS },
    "global": { value: null, matchMode: FilterMatchMode.CONTAINS }
  });

  const ufv = [
    'HLT500_V306701', 'HLT500_V306702', 'HLT500_V306703', 'HLT500_V306704', 'HLT500_V306705', 'HLT500_V306706', 'HLT500_V306707',
    'HLT500_V306708', 'HLT500_V306709'
  ]

  const statuses = [
    'ready', 'unready'
  ];

  const statusBodyTemplate = (rowData) => {
    return <span className={`customer-badge status-${rowData.Status}`}>{rowData.Status}</span>;
  }

  const statusItemTemplate = (option) => {
    return <span className={`customer-badge status-${option}`}>{option}</span>;
  }

  const statusRowFilterTemplate = (options) => {
    return <Dropdown value={options.value} options={statuses} onChange={(e) => options.filterApplyCallback(e.value)} itemTemplate={statusItemTemplate} placeholder="Select a Status" className="p-column-filter" showClear />;
  }

  const dropdownupdate = () => {
    return <Dropdown value={version} options={versionSelectItems} onChange={(e) => setversion(e.value)} placeholder="Select a Version" />
  }

  // Global Filter
  const [globalFilterValue, setGlobalFilterValue] = useState('');

  const onGlobalFilterChange = (e) => {
    const value = e.target.value;
    let _filters = { ...filters };
    _filters['global'].value = value;

    setFilters(_filters);
    setGlobalFilterValue(value);
  }

  // Clear Filter

  const clearFilter = () => {
    initFilters();
  }

  const initFilters = () => {
    setFilters({
      "IMEI": { value: null, matchMode: FilterMatchMode.START_WITH },
      "CFV": { value: null, matchMode: FilterMatchMode.START_WITH },
      "Status": { value: null, matchMode: FilterMatchMode.EQUALS },
      "global": { value: null, matchMode: FilterMatchMode.CONTAINS }
    });
    setGlobalFilterValue('');
  }

  const renderHeader = () => {
    return (
      <div className="flex justify-content-between">
        <Button type="button" icon="pi pi-filter-slash" label="Clear" className="p-button-outlined" onClick={clearFilter} />
        {/* <h2 class="text-white">Data_Table</h2> */}
        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Search..." />
        </span>
      </div>
    )
  }

  const header = renderHeader();

  // Firmware Selection

  const [version, setversion] = useState('');
  const versionSelectItems = [
    { label: 'HLT500_V306709', value: 'HLT500_V306709' },
    { label: 'HLT500_V306708', value: 'HLT500_V306708' },
    { label: 'HLT500_V306707', value: 'HLT500_V306707' },
    { label: 'HLT500_V306706', value: 'HLT500_V306706' },
    { label: 'HLT500_V306705', value: 'HLT500_V306705' },
    { label: 'HLT500_V306704', value: 'HLT500_V306704' },
    { label: 'HLT500_V306703', value: 'HLT500_V306703' },
    { label: 'HLT500_V306702', value: 'HLT500_V306702' },
    { label: 'HLT500_V306701', value: 'HLT500_V306701' },
  ];
  const input_select = (x) => {
    return (
      x = version
    );
  }

  // Selection

  const [selectedProducts, setSelectedProducts] = useState(null);
  console.log(selectedProducts)

  // Advance Filter

  const columns = [
    { field: 'IMEI', header: 'IMEI' },
    { field: 'category', header: 'Category' },
    { field: 'quantity', header: 'Quantity' }
  ];

  const [selectedColumns, setSelectedColumns] = useState([]);

  const onColumnToggle = (event) => {
    let selectedColumns = event.value;
    let orderedSelectedColumns = columns.filter(col => selectedColumns.some(sCol => sCol.field === col.field));
    setSelectedColumns(orderedSelectedColumns);
  }

  const Advance_filter = () => {
    return (
      <div style={{ textAlign: 'left' }}>
        <MultiSelect value={selectedColumns} placeholder="Advance Filter" options={columns} optionLabel="header" onChange={onColumnToggle} style={{ width: '20em' }} filter />
      </div>
    );
  }
  const adf = Advance_filter();

  const columnComponents = selectedColumns.map(col => {
    return <Column key={col.field} field={col.field} header={col.header} />;
  });

  // Button Update All
  const Button_Update = () => {
    const [visible, setVisible] = useState();
    const [accept, setAccept] = useState();
    const [reject, setReject] = useState();

    return (
      <>
        <ConfirmDialog visible={visible} onHide={() => setVisible(false)} message="Are you sure you want to proceed?"
          header="Confirmation" icon="pi pi-exclamation-triangle" accept={accept} reject={reject} />
        <Button label="Update All" onClick={() => setVisible(true)} className="p-button p-component p-button-info"></Button>
      </>
    );
  }

  const btu = Button_Update();

  // Radio Button

  const RadioButtonDemo = () => {
    const [city, setCity] = useState(null);

    return (
      <div>
        <div className="card">
          <h5>Basic</h5>
          <div className="field-radiobutton">
            <RadioButton inputId="city1" name="city" value="Chicago" onChange={(e) => setCity(e.value)} checked={city === 'Chicago'} />
            <label htmlFor="city1">Chicago</label>
          </div>
          <div className="field-radiobutton">
            <RadioButton inputId="city2" name="city" value="Los Angeles" onChange={(e) => setCity(e.value)} checked={city === 'Los Angeles'} />
            <label htmlFor="city2">Los Angeles</label>
          </div>
          <div className="field-radiobutton">
            <RadioButton inputId="city3" name="city" value="New York" onChange={(e) => setCity(e.value)} checked={city === 'New York'} />
            <label htmlFor="city3">New York</label>
          </div>
          <div className="field-radiobutton">
            <RadioButton inputId="city4" name="city" value="San Francisco" onChange={(e) => setCity(e.value)} checked={city === 'San Francisco'} />
            <label htmlFor="city4">San Francisco</label>
          </div>
        </div>
      </div>
    )
  }
  const rdbt = R

  return (
    <div class="datatable-filter-real">
      <div className="btu flex justify-content-between">{adf}{btu}</div>
      <DataTable
        value={products}
        responsiveLayout="scroll"
        paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown"
        dataKey="id"
        paginator
        emptyMessage="No data found."
        className="datatable-responsive"
        currentPageReportTemplate="Showing {first} to {last} of {totalRecords} posts"
        rows={10}
        showGridlines
        filters={filters}
        filterDisplay="row"
        header={header}
        resizableColumns
        columnResizeMode="expand"
        selectionMode="checkbox" selection={selectedProducts} onSelectionChange={e => setSelectedProducts(e.value)}
      // size="small"
