import React, { useState, useEffect } from 'react'
import axios from 'axios';

const ShowData_total = () => {

    const [api, setApi] = useState([]);
    const data_total = api.length;
    const acc_on = api.filter(obj => {
        return obj.acc == 1;
    })
    useEffect(() => {
        axios
            .get("http://localhost:3300/realtime_db")
            .then((res) => {
                setApi(res.data)
                // console.log(res.data)
            });

    }, []);
    
  return (
    <div>
        <text class="br">Total Data : {data_total}</text>
    </div>
  )
}

export default ShowData_total
